    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_register);

        FirebaseApp.initializeApp(Register.this);
        db = FirebaseFirestore.getInstance();
        firebaseAuth = FirebaseAuth.getInstance();
        cr = db.collection("UserDetails");


        nameR = findViewById(R.id.nameRegister);
        emailR = findViewById(R.id.emailRegister);
        passwordR = findViewById(R.id.passwordRegister);
        confirmPasswordR = findViewById(R.id.confirmPasswordRegister);
        next = findViewById(R.id.btnRegister);
        gotoLoginPage = findViewById(R.id.gotoLoginPage);
        mkLoader = findViewById(R.id.loaderR);

        next.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                final String emailRegister = emailR.getText().toString();
                final String passwordRegister = passwordR.getText().toString();

                if (emailRegister.isEmpty())
                    Toast.makeText(getApplicationContext(), "email empty", Toast.LENGTH_SHORT).show();
                if (emailRegister.isEmpty())
                    Toast.makeText(getApplicationContext(), "pass empty", Toast.LENGTH_SHORT).show();


                boolean check1 = isEmailValid(emailR.getText().toString());
                boolean check2 = isTextValid(nameR.getText().toString());

                if (passwordR.getText().toString().contains(" "))
                    Toast.makeText(Register.this, "Password should not contain any spaces", Toast.LENGTH_SHORT).show();
                else if (!(passwordR.getText().toString().equals(confirmPasswordR.getText().toString())))
                    Toast.makeText(Register.this, "Password Mismatch", Toast.LENGTH_SHORT).show();
                else if (!check1)
                    Toast.makeText(Register.this, "Incorrect Email format", Toast.LENGTH_SHORT).show();
                else if (!check2)
                    Toast.makeText(Register.this, "Incorrect Name format", Toast.LENGTH_SHORT).show();
                else {
//                    UserDetails dataBaseUser = new UserDetails(nameR.getText().toString(), emailRegister);
//                    cr.document(nameR.getText().toString()).set(dataBaseUser)
//                            .addOnSuccessListener(new OnSuccessListener<Void>() {
//                                @Override
//                                public void onSuccess(Void aVoid) {
//
//                                }
//                            })
                    mkLoader.setVisibility(View.VISIBLE);
                    firebaseAuth.createUserWithEmailAndPassword(emailRegister, passwordRegister)
                            .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
                                @Override
                                public void onComplete(@NonNull Task<AuthResult> task) {
                                    if (task.isSuccessful()) {
                                        Intent intent = new Intent(Register.this, PhoneAuthentication.class);
                                        intent.putExtra("nameR", nameR.getText().toString());
                                        intent.putExtra("emailR", emailR.getText().toString());
                                        startActivity(intent);
                                        finish();

                                    } else {
                                        mkLoader.setVisibility(View.GONE);
                                        Toast.makeText(Register.this, "Please Try Again", Toast.LENGTH_SHORT).show();
                                    }
                                }
                            });


                }
            }
        });

        gotoLoginPage.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                startActivity(new Intent(getApplicationContext(), Login.class));
                finish();
            }
        });

    }

    @Override
    public void onBackPressed() {
        if (doubleBackToExitPressedOnce) {
            Intent b = new Intent(Intent.ACTION_MAIN);
            b.addCategory(Intent.CATEGORY_HOME);
            b.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
            startActivity(b);
        }

        this.doubleBackToExitPressedOnce = true;
        Toast.makeText(this, "Press back again to EXIT", Toast.LENGTH_SHORT).show();

        new Handler().postDelayed(new Runnable() {

            @Override
            public void run() {
                doubleBackToExitPressedOnce = false;
            }
        }, 2000);
    }
